class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let s=t.newCommentDom(n.data.comment);$(`#comments-${e}`).prepend(s),t.deleteComment($(" .delete-comment-button",s)),new ToggleLike($(" .toggle-like-button",s)),new notie.alert({type:"success",text:"Comment Published !",stay:!1,time:2,position:"top"})},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                    <div class="comment-user">\n                        <div class="cmt-profile-img" style=" border-radius:50%; height: 50px; width: 50px; overflow:hidden">\n                            <img src="${e.user.avatar}" alt="${e.user.name}" style="width:100%; height:100%; border-radius:50%;">\n                        </div>\n                        <div class="cmt-profile-info">\n                            <p>\n                                ${e.user.name}\n                            </p>\n                            <small class="cmt-time">\n                                ${e.createdAt.toString().substring(4,21)}\n                            </small>\n                        </div>\n                        <small class="cmt-del-btn">\n                            <a class="delete-comment-button" href="/comments/destroy/${e.id}">DEL</a>\n                        </small>\n                    </div>\n                    <div class="comment-actions">\n                        <small>\n                            <a class="toggle-like-button" data-likes="${e.likes.length}" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                ${e.likes.length} Like\n                            </a>\n                        </small>\n                    </div>\n                    <div class="comment-content">\n                        <p>\n                            ${e.content}\n                        </p>\n                    </div>        \n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new notie.alert({type:"success",text:"Comment Published !",stay:!1,time:2,position:"top"})},error:function(e){console.log(e.responseText)}})}))}}