class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let o=parseInt($(e).attr("data-likes"));console.log("Like Count for this post",o),1==t.data.isDeleted?o-=1:o+=1,$(e).attr("data-likes",o),$(e).html(`${o} Likes`)})).fail((function(t){console.log("Error in Toggling the Like",t)}))}))}}